style:
#resumecv-layout {
  width: 100%;
  max-width: 210mm;
  padding: 0;
  box-shadow: 0 3px 1px -2px rgba(0,0,0,.2), 0 2px 2px 0 rgba(0,0,0,.14), 0 1px 5px 0 rgba(0,0,0,.12);
}

#resumecv-main{
font-size: 14px;line-height: 24px;
display: flex;
flex-wrap: nowrap;
padding: 30px;
padding-top: 10px;
padding-bottom: 0;
background-color: #ffff;

}

#resumecv-left .resumecv-block{
margin-bottom: 30px
}

.margin-20{
    margin-top: 20px;
}
#resumecv-left {resumecv-content-top
  width: 50%;
  padding:10px 5px;
  padding-right: 20px;
  padding-top: 20px;
  border-bottom: 2px solid #d4d8dd;
}
#resumecv-content {
  width: 50%;
  background: #fff;
  padding-left: 30px;
  padding-top: 20px;
  border-bottom: 2px solid #d4d8dd;
 
}


#resumecv-content-top{    
  background:#ffff;
  padding: 30px;
  padding-bottom: 10px;
  padding-top: 40px;
  display: flex;
}
   #resumecv-top-info-img{
         width:65%;
         
      }
      /* #resumecv-top-info-img .resumecv-top-info{
         padding-top: 34px;
         padding-left: 40px;
      } */
      

      #resumecv-box-ava img{
        width:100px;
      }
      #resumecv-top-info-contact{
        width: 35%;
        font-size: 16px;

      }
#resumecv-content-top .resumecv-block {
    position: relative;
    display: flex;
}

#resumecv-content-top .resumecv-block  img{
    width:80px;
    border-radius: 5%;
}

#resumecv-content .resumecv-block {
  position: relative;
  padding: 5px 10px;
}
#resumecv-left .resumecv-box-content {
padding-bottom: 0;
margin:0;
}
.skill{
margin-top: 15px
}

#resumecv-content-top h1{
font-size: 35px;    
margin:0;
padding-top: 0px;
line-height: normal;
letter-spacing: 3px;
text-transform: capitalize;
font-weight: 800;
}
#resumecv-content-top .job_position{
    font-size: 22px;
    font-weight: bold;
    margin-top: 15px;
    letter-spacing: 1px;

}

#resumecv-content-top h1 span{
display:block
}


.box-name p{        
  overflow: hidden;
}
.box-name{      
overflow: hidden;
background: #2f3a40;
}
#resumecv-content p{
margin-bottom: 5px
}
#resumecv-left .h3{
    font-size: 18px;
  font-weight: bold;
  margin-top: 0;
  text-transform: uppercase;
}
#resumecv-main .head{
  font-size: 18px;
  color: #2f3a40;
  padding-bottom: 5px;
  /* border-bottom: 2px solid #9B9C8F; */
  font-weight: bold;
  margin-top: 0;
  text-transform: uppercase;
}

.resumecv-top-bottom{ 
  background:#ffff;
  padding: 10px 30px 10px 30px;
  display: flex;
}
.resumecv-block-footer{
    border: 0px;
}
.resumecv-top-bottom .head{
  font-size: 18px;
  color: #2f3a40;
  padding-bottom: 5px;
  padding-top: 10px;
  /* border-bottom: 2px solid #9B9C8F; */
  font-weight: bold;
  margin-top: 0;
  text-transform: uppercase;
}
.resumecv-top-bottom h3{
  font-weight:normal;font-size: 14px;line-height:30px;margin-bottom: 10px;color: #333;
}


#resumecv-content span[contenteditable]{
display: inline-block;
padding-right: 15px;
padding-left: 15px;
}

.row-col-data {
    float: left;
}
#resumecv-content .row-col-data .row-details{
    padding-bottom: 50px;
}
.row-title {
    font-style: italic;
    padding-left: 20px;
    background-size: 9px;
    background-position: 0 4px;
    background-repeat: no-repeat;
    width: 100%;
}
.row-details {
    padding: 7px 0 7px 20px;
    background-size: 9px 100%;
    background-position: 0 0;
    background-repeat: no-repeat;
}
#resumecv-left .h3:first-child{
margin-top: 0
}
.resumecv-box-content{
margin-bottom: 20px;
position: relative;
font-size: 16px;

}
#resumecv-main h3{
  font-weight:normal;font-size: 14px;line-height:30px;margin-bottom: 10px;color: #333;
}
#resumecv-box-ava{    
text-align:center;padding:2px;margin-bottom: 10px; auto;
}
#resumecv-box-ava img{
padding: 0;
border:0;
width: 150px;
height: 150px;

}
.bar-exp span {
  display:block;
  height: 8px;
  background: #d9d9d9;
}
.bar-exp{
background: #2f3a40;
border: solid 2px white;
  margin-bottom: 12px;
}
.icoweb label{
display: block;
color:#627e88;
}
.icoweb span{
display: block;
}

.box-contact{
font-size: 13px;
padding-top:0px;
}

.icoweb i.fa{
  width: 20px;
  height: 20px;
  background-color:#ffff;
  border-radius: 50%;
  -webkit-border-radius: 50%;
  -moz-border-radius: 50%;
  display: inline-block;
  text-align: center;
  font-size: 13px;
  line-height: 20px;
  color: #2f3a40!important;
  margin-right: 10px;
  margin-top: 3px;float:left
}
.head i{
  font-size: 25px;
  margin-right: 10px;
}

#resumecv-content span.exp-date {
  float: right;
}
.icon_fa{
text-align: center;
}
.icon_fa i.fa{
font-size: 20px;
border: solid 1px #ffffff;
border-radius: 50%;
padding: 5px;
width: 20px;
color: white;
background: #ffffff;
margin-bottom: 5px;
}
#resumecv-top-info-contact hr {
  border: 1px solid #d4d8dd;
}
p.head i.fa{float:left}
